export const createSVG = (width, color, maxWidth, className) => {
  const ref = color.replace("#", "");
  const svgId = `hedgie_${ref}`;
  const svgIdRef = `#${svgId}`;

  const svg =
    color === "#ffffff"
      ? whiteTemplate(width, svgId, svgIdRef, maxWidth)
      : genericTemplate(color, width, svgId, svgIdRef, maxWidth, className);

  return { __html: svg };
};

